import{_ as e}from"./_plugin-vue_export-helper.cdc0426e.js";import{o as p,c as t,f as o}from"./app.4da52b98.js";const r={},s=o('<h1 id="\u5173\u4E8E\u300C\u7B97\u6CD5\u5DE5\u7A0B\u5E08\u300D\u804C\u4E1A\u53D1\u5C55\u7684\u601D\u8003" tabindex="-1"><a class="header-anchor" href="#\u5173\u4E8E\u300C\u7B97\u6CD5\u5DE5\u7A0B\u5E08\u300D\u804C\u4E1A\u53D1\u5C55\u7684\u601D\u8003" aria-hidden="true">#</a> \u5173\u4E8E\u300C\u7B97\u6CD5\u5DE5\u7A0B\u5E08\u300D\u804C\u4E1A\u53D1\u5C55\u7684\u601D\u8003</h1><hr><blockquote><p>\u8F6C\u81EA[Hulu\u7814\u53D1\u526F\u603B\u88C1\u8BF8\u845B\u8D8A]\u8BB2\u6F14\u7A3F</p></blockquote><p>\u8FD1\u5E74\u6765\u4EBA\u5DE5\u667A\u80FD\u6280\u672F\u53D6\u5F97\u4E86\u5DE8\u5927\u7684\u7A81\u7834\uFF0C\u5E94\u7528\u5230\u5DE5\u4E1A\u754C\u7684\u573A\u666F\u4E5F\u8D8A\u6765\u8D8A\u591A\uFF0C\u5438\u5F15\u4E86\u5927\u91CF\u7684\u4EBA\u624D\u6D8C\u8FDB\u7B97\u6CD5\u7814\u7A76\u9886\u57DF\u3002\u6211\u8BA4\u8BC6\u7684\u5F88\u591A\u670B\u53CB\u4E5F\u6295\u8EAB\u5176\u4E2D\uFF0C\u5728\u5B66\u672F\u754C\u505A\u7814\u7A76\u7684\u6709\uFF0C\u5728\u5DE5\u4E1A\u754C\u641E\u5E94\u7528\u7814\u7A76\u7684\u4E5F\u6709\u3002\u540C\u65F6\u56E0\u4E3A\u5DE5\u4F5C\u7684\u539F\u56E0\uFF0C\u6211\u4E5F\u63A5\u89E6\u4E86\u5F88\u591A\u7B97\u6CD5\u65B9\u5411\u7684\u5019\u9009\u4EBA\uFF0C\u6709\u4E9B\u662F\u5B66\u751F\uFF0C\u6709\u4E9B\u662F\u6765\u81EA\u4E8E\u5DE5\u4E1A\u754C\u7684\u5176\u4ED6\u516C\u53F8\u3002\u66F4\u6DF1\u7684\u63A5\u89E6\u5219\u662F\u6765\u81EA\u4E8E\u4E0EHulu\u5185\u90E8\u7B97\u6CD5\u56E2\u961F\u7684\u540C\u4E8B\u4EEC\uFF0C\u4E0D\u8BBA\u662F\u65E5\u5E38\u7684\u7814\u7A76\u8FD8\u662F\u4EBA\u5458\u664B\u5347\u65F6\u8BA9\u6211\u6709\u673A\u4F1A\u548C\u5927\u5BB6\u6709\u5F88\u591A\u6DF1\u5165\u7684\u4EA4\u6D41\u8BA8\u8BBA\uFF0C\u5176\u4E2D\u5C31\u5305\u62EC\u4E86\u7B97\u6CD5\u5DE5\u7A0B\u5E08/\u7814\u7A76\u5458\u5728\u5DE5\u4E1A\u754C\u804C\u4E1A\u53D1\u5C55\u7684\u95EE\u9898\u3002\u7B97\u6CD5\u5DE5\u7A0B\u5E08/\u7814\u7A76\u5458\u4F5C\u4E3A\u4E00\u4E2A\u6BD4\u8F83\u65B0\u7684\u804C\u4E1A\uFF0C\u8FD8\u6CA1\u6709\u6210\u719F\u7684\u804C\u4E1A\u53D1\u5C55\u8DEF\u5F84\u505A\u53C2\u8003\uFF0C\u6240\u4EE5\u6211\u5728\u8FD9\u4E2A\u9886\u57DF\u7684\u5F88\u591A\u670B\u53CB\u3001\u540C\u4E8B\u90FD\u4F1A\u9047\u5230\u8FD9\u4E2A\u95EE\u9898\uFF1A<strong>&quot;\u63A5\u4E0B\u6765\uFF0C\u6211\u8BE5\u600E\u4E48\u529E\uFF1F\u6211\u7684\u51FA\u8DEF\u6709\u54EA\u4E9B\uFF1F&quot;\u6211\u4ECA\u5929\u7684\u5206\u4EAB\u4E0D\u4F1A\u7ED9\u5927\u5BB6\u6307\u51FA\u5177\u4F53\u7684\u8DEF\u5F84\uFF0C\u56E0\u4E3A\u6BCF\u4E2A\u4EBA\u548C\u6BCF\u4E2A\u516C\u53F8\u90FD\u4E0D\u4E00\u6837\uFF0C\u4F46\u5E0C\u671B\u901A\u8FC7\u63D0\u51FA\u4E00\u4E9B\u5177\u4F53\u7684\u95EE\u9898\uFF0C\u5F15\u53D1\u7B97\u6CD5\u5DE5\u7A0B\u5E08/\u7814\u7A76\u5458\u7FA4\u4F53\u5BF9\u672A\u6765\u804C\u4E1A\u53D1\u5C55\u7684\u601D\u8003</strong>\uFF0C\u6700\u7EC8\u627E\u5230\u4E00\u6761\u9002\u5408\u81EA\u5DF1\u7684\u804C\u4E1A\u53D1\u5C55\u8DEF\u5F84\u3002\u6211\u4ECA\u5929\u7684\u5206\u4EAB\u4F1A\u5206\u4E3A\u4E09\u90E8\u5206\uFF1A</p><ul><li><strong>\u516C\u53F8\u7EC4\u7EC7\u67B6\u6784</strong></li><li><strong>\u4EBA\u624D\u6A21\u578B</strong></li><li><strong>\u503C\u5F97\u601D\u8003\u7684\u95EE\u9898</strong></li></ul><h2 id="_01-\u516C\u53F8\u7EC4\u7EC7\u67B6\u6784" tabindex="-1"><a class="header-anchor" href="#_01-\u516C\u53F8\u7EC4\u7EC7\u67B6\u6784" aria-hidden="true">#</a> <strong>01.\u516C\u53F8\u7EC4\u7EC7\u67B6\u6784</strong></h2><p>\u5F88\u591A\u4EBA\u53EF\u80FD\u4F1A\u6709\u7591\u95EE\uFF0C\u804A\u4E2A\u4EBA\u804C\u4E1A\u53D1\u5C55\u4E3A\u4EC0\u4E48\u8981\u5148\u4ECB\u7ECD\u516C\u53F8\u7684\u7EC4\u7EC7\u67B6\u6784\uFF1F\u5B83\u548C\u804C\u4E1A\u53D1\u5C55\u6709\u4EC0\u4E48\u5173\u7CFB\u5462\uFF1F\u6211\u60F3\u8BF4\u4E0D\u4EC5\u6709\u5173\u7CFB\uFF0C\u5173\u7CFB\u8FD8\u633A\u5927\u3002\u9996\u5148\uFF0C\u6BCF\u4E2A\u516C\u53F8\u7684\u7EC4\u7EC7\u67B6\u6784\u90FD\u4E0D\u540C\uFF0C\u4E0D\u540C\u7684\u67B6\u6784\u51B3\u5B9A\u4E86\u4E0D\u540C\u7684\u664B\u5347\u901A\u9053\uFF0C\u7B2C\u4E8C\uFF0C\u804C\u4E1A\u8DEF\u5F84\u89C4\u5212\u662F\u4E3A\u4E86\u5728\u804C\u573A\u4E0A\u8D70\u7684\u66F4\u9AD8\u66F4\u8FDC\uFF0C\u6240\u4EE5\u8131\u79BB\u5B9E\u9645\u7684\u664B\u5347\u901A\u9053\u8C08\u89C4\u5212\u662F\u4E0D\u5B9E\u9645\u7684\u3002\u4F60\u5728\u505A\u804C\u4E1A\u8DEF\u5F84\u89C4\u5212\u65F6\uFF0C\u9996\u5148\u5C31\u8981\u4E86\u89E3\u6240\u5904\u516C\u53F8\u7684\u67B6\u6784\u60C5\u51B5\uFF0C\u660E\u786E\u53EF\u80FD\u7684\u804C\u4E1A\u8DEF\u5F84\u6709\u54EA\u4E9B\uFF0C\u7136\u540E\u624D\u80FD\u786E\u7ACB\u76EE\u6807\u5E76\u4E3A\u4E4B\u8BA1\u5212\u548C\u6267\u884C\u3002\u63A5\u4E0B\u6765\u6211\u5C31\u7B80\u5355\u4ECB\u7ECD\u4E0B\u4E09\u79CD\u5E38\u89C1\u7684\u7EC4\u7EC7\u67B6\u6784\uFF1A</p><p><strong>\u7B2C\u4E00\u79CD</strong>\u662FAI LAB\u3002\u8FD9\u79CD\u6A21\u5F0F\u5728\u5927\u516C\u53F8\u5F88\u5E38\u89C1\uFF0C\u8001\u724C\u7684\u6709\u4EE5MSRA\u4E3A\u4EE3\u8868\u7684\u5916\u4F01\u7814\u7A76\u673A\u6784\uFF0C\u56FD\u5185\u7684\u5982\u767E\u5EA6\u7684\u6DF1\u5EA6\u5B66\u4E60\u7814\u7A76\u9662\u3001\u963F\u91CC\u8FBE\u6469\u9662\u3001\u5934\u6761\u7684AI LAB\u7B49\u3002AI Lab\u53EF\u4EE5\u7B97\u662F\u5DE5\u4E1A\u754C\u4E2D\u9876\u7EA7\u7684\u4EBA\u5DE5\u667A\u80FD\u7814\u7A76\u673A\u6784\uFF0C\u6C47\u805A\u4E86\u4F17\u591A\u7684\u4E1A\u754C\u5927\u725B\u548C\u9876\u5C16\u4EBA\u624D\uFF0C\u505A\u51FA\u5F88\u591A\u6839\u690D\u4E8E\u5DE5\u4E1A\u754C\u7684\u7814\u7A76\u8D21\u732E\u3002\u5728\u8FD9\u6837\u7684\u7ED3\u6784\u4E0B\uFF0C\u4F60\u5411\u4E0A\u8D70\u9700\u8981\u5177\u5907\u7684\u6280\u80FD\u548C\u4EA7\u51FA\u3001\u80FD\u8FBE\u5230\u7684\u9AD8\u5EA6\u80AF\u5B9A\u4E0E\u5728\u7814\u53D1\u7EBF\u7684\u540C\u5B66\u662F\u4E0D\u4E00\u6837\u7684\u3002</p><img src="https://www.hualigs.cn/image/60b89081533b2.jpg" style="zoom:60%;" align="center"><p><strong>\u7B2C\u4E8C\u79CD</strong>\u7684AI\u56E2\u961F\u662F\u5728\u7814\u53D1\u7EBF\uFF0C\u4F4D\u4E8ECTO\u4E0B\u9762\uFF0C\u652F\u6301\u4E0D\u540C\u4EA7\u54C1\u7EBF\u548C\u516C\u53F8\u7EA7\u522B\u7684AI\u9700\u6C42\u3002\u5BF9\u8FD9\u4E2A\u67B6\u6784\u4E0B\u7684AI\u56E2\u961F\uFF0C\u516C\u53F8\u5E0C\u671B\u5B83\u63D0\u4F9B\u7684\u662F\u4E00\u79CD\u901A\u7528\u7684\u7C7B\u4F3C\u4E8E\u4E2D\u53F0\u7684\u80FD\u529B\u3002</p><img src="https://www.hualigs.cn/image/60b8908154101.jpg" style="zoom:60%;" align="center"><p><strong>\u7B2C\u4E09\u79CD</strong>\u7684AI\u56E2\u961F\u4F4D\u4E8E\u4E0D\u540C\u7684\u4EA7\u54C1\u7EBF\u4E0A\uFF0C\u8FD9\u79CD\u7ED3\u6784\u5728\u5927\u516C\u53F8\u5E38\u89C1\u3002\u4EA7\u54C1\u7EBF\u6BD4\u8F83\u4E30\u5BCC\uFF0C\u4E14\u7528\u6237\u91CF\u8F83\u5927\uFF0C\u56E0\u6B64\u9700\u8981\u6709\u4E13\u95E8\u7684AI\u56E2\u961F\u8FDB\u884C\u652F\u6301\u3002\u6BD4\u5982\u5728\u73B0\u573A\u4E0E\u6211\u6C9F\u901A\u7684\u4E00\u4E2A\u5C0F\u670B\u53CB\uFF0C\u4ED6\u662F\u817E\u8BAF\u770B\u4E00\u770B\u56E2\u961F\u7684\u7B97\u6CD5\u5B9E\u4E60\u751F\uFF0C\u542C\u4ED6\u4ECB\u7ECD\u4E86\u89E3\u5230\u817E\u8BAF\u91CC\u9762\u56E2\u961F\u5206\u5DE5\u5F88\u7EC6\u81F4\uFF0C\u7B97\u6CD5\u56E2\u961F\u548C\u5DE5\u7A0B\u56E2\u961F\u662F\u5206\u5F00\u7684\uFF0C\u7B97\u6CD5\u56E2\u961F\u53C8\u5206\u4E3A\u72EC\u7ACB\u7684\u53EC\u56DE\u56E2\u961F\u548C\u6392\u5E8F\u56E2\u961F\uFF0C\u6BCF\u4E2A\u56E2\u961F\u5728\u81EA\u5DF1\u7684\u65B9\u5411\u4E0A\u505A\u7684\u5F88\u7CBE\u7EC6\u3002</p><img src="https://www.hualigs.cn/image/60b88fbd48132.jpg" style="zoom:60%;" align="center"><p>\u4EE5\u4E0A\u4E09\u79CD\u7ED3\u6784\u662F\u6BD4\u8F83\u666E\u904D\u7684\uFF0C\u5927\u5BB6\u53EF\u4EE5\u5BF9\u7167\u60F3\u4E00\u60F3\u4F60\u4EEC\u56E2\u961F\u5728\u54EA\u4E2A\u67B6\u6784\u4E0B\u9762\uFF0C\u4F60\u53C8\u5728\u56E2\u961F\u7684\u4EC0\u4E48\u4F4D\u7F6E\u4E0A\u3002\u4E24\u4E09\u5E74\u540E\u4F60\u80AF\u5B9A\u662F\u60F3\u5F80\u4E0A\u664B\u5347\u7684\uFF0C\u90A3\u5728\u73B0\u6709\u7684\u664B\u5347\u901A\u9053\u4E0B\u9700\u8981\u5177\u5907\u4EC0\u4E48\u6837\u7684\u6280\u80FD\u548C\u4EA7\u51FA\u5462\uFF1F\u5728\u5411\u4E0A\u5462\uFF0C\u8FD8\u9700\u8981\u600E\u4E48\u505A\uFF1F\u7EE7\u7EED\u5411\u4E0A\u53C8\u5982\u4F55\u5462\uFF1F\u8FD9\u4E9B\u90FD\u662F\u9700\u8981\u5927\u5BB6\u63D0\u524D\u601D\u8003\u7684\u3002</p><img src="https://www.hualigs.cn/image/60b8908144500.jpg" style="zoom:60%;" align="center"><h2 id="_02-\u4EBA\u624D\u6A21\u578B" tabindex="-1"><a class="header-anchor" href="#_02-\u4EBA\u624D\u6A21\u578B" aria-hidden="true">#</a> 02.\u4EBA\u624D\u6A21\u578B</h2><img src="https://www.hualigs.cn/image/60b88fbd3dd41.jpg" style="zoom:60%;" align="center"><p>\u63A5\u4E0B\u6765\u7ED9\u5927\u5BB6\u4ECB\u7ECD\u4E00\u4E0B\u4EBA\u624D\u6A21\u578B\uFF0C\u5728\u7ED9\u5B9A\u4E86\u516C\u53F8\u67B6\u6784\u540E\uFF0C\u4F60\u8981\u600E\u4E48\u505A\u5462\uFF1F\u5927\u5BB6\u53EF\u4EE5\u770B\u8FD9\u4E2A\u56FE\uFF0C\u4E00\u4E2A\u5927\u5199\u7684\u5B57\u6BCD\u201CT\u201D\u3002\u8FD9\u662F\u6211\u7ECF\u5E38\u7ED9Hulu\u7684\u540C\u4E8B\u8BB2\u7684\uFF0C\u6211\u4EEC\u8981\u60F3\u8C61\u6211\u4EEC\u7684\u6280\u80FD\u5C31\u50CF\u4E00\u4E2A\u201CT-shaped\u201D\u3002\u4E5F\u53EF\u4EE5\u6BD4\u55BB\u4E3A\u5927\u5BB6\u719F\u8BC6\u7684\u201Cwide&amp;deep\u201D\u7B97\u6CD5\uFF0C\u5C31\u662F\u4F60\u8981\u8D70\u591A\u5BBD\uFF0C\u4F60\u8981\u8D70\u591A\u6DF1\u3002</p><img src="https://www.hualigs.cn/image/60b88fbd4faeb.jpg" style="zoom:60%;" align="center"><p>\u6709\u4E00\u7C7B\u4EBA\uFF0C\u5C24\u5176\u662F\u6211\u4EEC\u4ECA\u5929\u7684\u53D7\u4F17\uFF0C\u5E94\u8BE5\u662F\u503E\u5411\u4E8E\u504F\u6DF1\u7684\uFF0C\u4ECE\u800C\u6210\u4E3A\u4E00\u4E2A\u4E13\u5BB6\u578B\u4EBA\u624D\u3002\u6BD4\u5982\u6709\u4E00\u4E2A\u4EBA\uFF0C\u4ED6\u60F3\u6210\u4E3A\u4E00\u4E2A\u4E13\u5BB6\uFF0C\u6211\u4EEC\u5BF9\u8BDD\u5982\u4E0B\uFF1A</p><blockquote><p>\u6211\u95EE\uFF1A5\u5E74\u4E4B\u540E\u4F60\u60F3\u5E72\u4EC0\u4E48\uFF1F \u4ED6\u7B54\uFF1A\u6211\u60F3\u505A\u4E00\u4E2A\u4E13\u5BB6\uFF1B \u6211\u95EE\uFF1A\u90A310\u5E74\u4E4B\u540E\u5462\uFF1F \u4ED6\u7B54\uFF1A\u66F4\u4E13\u7684\u4E13\u5BB6\u3002</p></blockquote><p>\u60F3\u6210\u4E3A\u4E13\u5BB6\u5F88\u597D\uFF0C\u4F46\u53EA\u8BF4\u4E13\u5BB6\u662F\u4E0D\u591F\u7684\uFF0C\u5047\u5982\u8BF4\u8981\u6210\u4E3AMachine Learning\u7684\u4E13\u5BB6\uFF0C\u597D\u50CF\u6709\u70B9\u592A\u6CDB\u4E86\u3002\u6240\u4EE5\u63A5\u4E0B\u6765\u8FD8\u8981\u95EE\u81EA\u5DF1\u53E6\u5916\u4E00\u4E2A\u95EE\u9898\uFF0C\u90A3\u5C31\u662F\u6211\u8981\u201C\u4E13\u201D\u4EC0\u4E48\uFF1F\u5BF9\u4E8E\u6280\u672F\u540C\u5B66\u6765\u8BB2\uFF0C\u5927\u6982\u6709\u4E09\u4E2A\u7EF4\u5EA6\u4E0A\u7684\u4E13\u5BB6\uFF1A</p><p>\u7B2C\u4E00\uFF0C<strong>\u67D0\u4E00\u9879\u6280\u672F\u7684\u4E13\u5BB6</strong>\u3002\u6BD4\u5982\u8BF4\u6211\u662F\u5F3A\u5316\u5B66\u4E60\u65B9\u5411\u7684\u4E13\u5BB6\uFF0C\u6211\u5BF9\u5F3A\u5316\u5B66\u4E60\u6F14\u8FDB\u7684\u5386\u53F2\u7279\u522B\u6E05\u695A\uFF0C\u8FC7\u5F80\u7684\u6280\u672F\u70B9\u6709\u54EA\u4E9B\uFF0C\u672A\u6765\u53EF\u80FD\u7684\u7814\u7A76\u8D8B\u52BF\u6709\u54EA\u4E9B\uFF0C\u66F4\u91CD\u8981\u7684\u662F\u6211\u8FD8\u4E3A\u8FD9\u9879\u6280\u672F\u7684\u53D1\u5C55\u505A\u51FA\u8FC7\u67D0\u4E9B\u91CD\u8981\u7684\u8D21\u732E\u3002\u90A3\u6211\u5C31\u7B97\u662F\u5F3A\u5316\u5B66\u4E60\u7684\u4E13\u5BB6\u4E86\uFF0C\u8FD9\u662F\u4E00\u4E2A\u5F88\u786E\u5B9A\u7684\u3002</p><p>\u7B2C\u4E8C\uFF0C<strong>\u67D0\u4E00\u6280\u672F\u9886\u57DF\u7684\u4E13\u5BB6</strong>\u3002\u6BD4\u5982\u8BF4CV\u662F\u4E00\u4E2A\u6280\u672F\u9886\u57DF\uFF0C\u8FD9\u4E2A\u9886\u57DF\u91CC\u6709\u5F88\u591A\u7279\u5B9A\u7684\u7B97\u6CD5\uFF0C\u867D\u7136\u6211\u4E0D\u662F\u67D0\u4E00\u7279\u5B9A\u7B97\u6CD5\u7684\u4E13\u5BB6\uFF0C\u4F46\u662F\u6211\u90FD\u4E86\u89E3\uFF0C\u66F4\u5173\u952E\u7684\u662F\u6211\u53EF\u4EE5\u5229\u7528\u8FD9\u4E9B\u6210\u719F\u7684\u7B97\u6CD5\u89E3\u51B3\u7279\u5B9A\u7684\u4E1A\u52A1\u95EE\u9898\uFF0C\u8FD9\u4E5F\u662F\u4E00\u7C7B\u4E13\u5BB6\uFF1B\u4F8B\u5982\u63A8\u8350\u7CFB\u7EDF\u4E5F\u662F\u4E00\u4E2A\u9886\u57DF\uFF0C\u4E5F\u53EF\u4EE5\u6210\u4E3A\u8FD9\u4E2A\u9886\u57DF\u7684\u4E13\u5BB6\u3002</p><p>\u7B2C\u4E09\uFF0C<strong>\u67D0\u4E00\u5546\u4E1A\u9886\u57DF\u7684\u6280\u672F\u4E13\u5BB6</strong>\u3002\u6BD4\u5982\u8BF4\u62FF\u5546\u4E1A\u5E7F\u544A\u6765\u8BF4\uFF0C\u5B83\u5305\u542B\u4E86\u4E00\u6574\u5957\u7684\u6280\u672F\uFF0C\u4F8B\u5982\u8BF4\u600E\u4E48\u4F18\u5316\u7F51\u4E0A\u5E7F\u544A\u7684\u914D\u578B\uFF0C\u6BD4\u5982\u8BF4\u600E\u4E48\u505A\u5E7F\u544A\u7684\u4E2A\u6027\u5316\u63A8\u8350\u7B49\uFF0C\u4F60\u4E0D\u4EC5\u90FD\u77E5\u9053\u8FD8\u53EF\u4EE5\u5E26\u9886\u56E2\u961F\u641E\u5B9A\u6574\u4E2A\u7CFB\u7EDF\u7684\u95EE\u9898\uFF0C\u6240\u4EE5\u4F60\u4E5F\u53EF\u4EE5\u6210\u4E3A\u8FD9\u4E00\u5546\u4E1A\u9886\u57DF\u7684\u7B97\u6CD5\u4E13\u5BB6\u3002</p><p><strong>\u5982\u679C\u4F60\u60F3\u6210\u4E3A\u4E00\u4E2A\u4E13\u5BB6\uFF0C\u90A3\u5230\u5E95\u662F\u4F60\u60F3\u6210\u4E3A\u4E0A\u8FF0\u4E09\u79CD\u7684\u54EA\u4E00\u79CD\u5462\uFF1F\u6709\u8FC7\u601D\u8003\u5417\uFF1F</strong></p><img src="https://www.hualigs.cn/image/60b88fbd4f0f3.jpg" style="zoom:60%;" align="center"><p>\u6211\u4EEC\u63A5\u4E0B\u6765\u8BF4\u7B2C\u4E8C\u7C7B\uFF0C\u4E0E\u4E13\u5BB6\u76F8\u5BF9\u5E94\u7684\u662F\u504F\u5BBD\u5EA6\uFF0C\u7B80\u79F0\u4E3A\u201C\u6742\u5BB6\u201D\u5427\uFF0C\u4E5F\u6709\u4E09\u4E2A\u7EF4\u5EA6\uFF1A</p><p>\u7B2C\u4E00\uFF0C<strong>\u5168\u80FD\u578B\u7B97\u6CD5\u5DE5\u7A0B\u5E08</strong>\uFF1B\u8FD9\u7C7B\u5DE5\u7A0B\u5E08\u7684\u5B66\u4E60\u80FD\u529B\u7279\u522B\u5F3A\uFF0C\u7ED9\u4ED6\u4EFB\u4F55\u4E00\u4E2A\u95EE\u9898\uFF0C\u90FD\u80FD\u901A\u8FC7\u5FEB\u901F\u5B66\u4E60\u638C\u63E1\u76F8\u5173\u77E5\u8BC6\u70B9\uFF0C\u7136\u540E\u628A\u95EE\u9898\u89E3\u51B3\uFF1B\u518D\u629B\u7ED9\u4ED6\u53E6\u4E00\u4E2A\u5B8C\u5168\u4E0D\u540C\u7684\u95EE\u9898\uFF0C\u4F9D\u7136\u53EF\u4EE5\u5FEB\u901F\u89E3\u51B3\u3002\u5176\u5B9E\u5728\u67D0\u4E9B\u7C7B\u578B\u7684\u4F01\u4E1A\u6216\u8005\u67D0\u4E9B\u9636\u6BB5\u7684\u4F01\u4E1A\uFF0C\u7ED9\u7B97\u6CD5\u5DE5\u7A0B\u5E08\u8425\u9020\u7684\u5DE5\u4F5C\u5B66\u4E60\u73AF\u5883\u5C31\u662F\u8FD9\u6837\u7684\uFF0C\u4F60\u6CA1\u6709\u529E\u6CD5\u51B3\u5B9A\u7CBE\u4E13\u4E00\u4E2A\u65B9\u5411\uFF0C\u56E0\u4E3A\u6BCF\u5929\u90FD\u8981\u9762\u5BF9\u5404\u79CD\u5C42\u51FA\u4E0D\u7A77\u7684\u95EE\u9898\uFF0C\u4E14\u90FD\u9700\u8981\u4F60\u5C3D\u5FEB\u89E3\u51B3\u3002\u4F46\u662F\u5927\u5BB6\u53EF\u4EE5\u60F3\u8C61\u5728\u8FD9\u79CD\u73AF\u5883\u957F\u671F\u53D1\u5C55\u4E0B\u53BB\u7684\u8BDD\uFF0C\u5BF9\u7B97\u6CD5\u5DE5\u7A0B\u5E08\u957F\u671F\u53D1\u5C55\u4F1A\u9020\u6210\u4EC0\u4E48\u5F71\u54CD\uFF1B</p><p>\u7B2C\u4E8C\uFF0C\u53EF\u80FD\u4E0D\u5B8C\u5168\u5305\u542B\u7B97\u6CD5\u5DE5\u7A0B\u5E08\uFF0C\u5728\u7279\u5B9A\u7684\u9886\u57DF\u5185\uFF0C\u4F60\u5BF9\u6570\u636E\u5168\u6D41\u7A0B\u90FD\u4E86\u89E3\uFF0C\u5BF9\u6570\u636E\u600E\u4E48\u4ECE\u5934\u5230\u5C3E\u5E94\u7528\u5230\u673A\u5668\u5B66\u4E60\u9886\u57DF\u90FD\u4E86\u89E3\uFF0C\u6709\u70B9<strong>\u6570\u636E\u79D1\u5B66\u5BB6</strong>\u7684\u610F\u601D\u3002</p><p>\u7B2C\u4E09\u7C7B\uFF0C\u5728\u5927\u5382\u6216\u8005\u5927\u5E73\u53F0\u6709\u8FC7\u4E30\u5BCC\u7684\u7ECF\u9A8C\uFF0C\u7528\u540C\u6837\u7684\u7B97\u6CD5\u89E3\u51B3\u4E0D\u540C\u91CF\u7EA7\u7684\u95EE\u9898\uFF1B\u76F8\u8F83\u4E8E\u5176\u4ED6\u6CA1\u6709\u76F8\u5173\u7ECF\u5386\u7684\u7B97\u6CD5\u5DE5\u7A0B\u5E08\uFF0C\u4F60\u77E5\u9053\u600E\u4E48\u53BB\u5904\u7406\u66F4\u5927\u7684\u95EE\u9898\u9762\u5BF9\u66F4\u5927\u7684\u6311\u6218\uFF0C\u8FD9\u662F\u5F88\u91CD\u8981\u7684\u4F18\u52BF\u3002</p><p>\u603B\u7ED3\u6765\u8BF4\uFF0C\u5373\u4F7F\u8BF4\u4F60\u4EC0\u4E48\u90FD\u80FD\u5E72\uFF0C\u4E5F\u8981\u60F3\u4E00\u60F3\u662F\u5728\u4E00\u4E2A\u4EC0\u4E48\u6837\u7684\u6846\u67B6\u4E0B\u5904\u7406\u4EC0\u4E48\u6837\u7684\u95EE\u9898\u3002</p><img align="center" src="https://www.hualigs.cn/image/60b891eb365da.jpg" style="zoom:60%;"><p>\u518D\u8BF4\u7B2C\u4E09\u7C7B\uFF0C\u8FD8\u6709\u4E9B\u4EBA\u5728\u7279\u5B9A\u9636\u6BB5\u8981\u8D70\u5411\u7BA1\u7406\uFF0C\u8F6C\u5411\u7BA1\u7406\u9700\u8981\u5177\u5907\u7684\u6280\u80FD\u4E0E\u524D\u4E24\u7C7B\u53C8\u4E0D\u4E00\u6837\u3002</p><p>\u5176\u5B9E\u5F53\u4F60\u5DF2\u7ECF\u662F\u4E00\u4E2A\u9886\u57DF\u7684\u4E13\u5BB6\u540E\uFF0C\u5927\u5BB6\u90FD\u4F1A\u5728\u7EE7\u7EED\u8D70\u4E13\u5BB6\u7EBF\u8FD8\u662F\u8D70\u5411\u7BA1\u7406\u7EBF\u95F4\u6709\u8FC7\u601D\u8003\u548C\u6447\u6446\u3002\u4F46\u662F\u4E0D\u7BA1\u4F60\u6700\u7EC8\u8D70\u5411\u4F55\u65B9\uFF0C\u4E00\u5B9A\u8981\u77E5\u9053\u9002\u5408\u7684\u624D\u662F\u6700\u597D\u7684\u3002\u600E\u4E48\u5224\u65AD\u81EA\u5DF1\u66F4\u9002\u5408\u54EA\u4E2A\u5462\uFF1F\u90A3\u5C31\u9700\u8981\u628A\u4E13\u5BB6\u7EBF\u548C\u7BA1\u7406\u7EBF\u5BF9\u4EBA\u624D\u6A21\u578B\u7684\u8981\u6C42\u641E\u6E05\u695A\u3002\u4E13\u5BB6\u7EBF\u524D\u9762\u5DF2\u7ECF\u8BF4\u8FC7\uFF0C\u6211\u4EEC\u5728\u4ECB\u7ECD\u4E0B\u7BA1\u7406\u4EBA\u624D\u9700\u8981\u5177\u5907\u7684\u6280\u80FD\u3002</p><p>\u7B2C\u4E00\u70B9\uFF0C\u201C\u5B66\u800C\u4F18\u5219\u4ED5\u201D\u3002\u600E\u4E48\u7406\u89E3\u5462\uFF1F\u53EA\u6709\u90A3\u4E9B\u5DF2\u7ECF\u6210\u4E3A\u6280\u672F\u4E13\u5BB6\u7684\u4EBA\uFF0C\u624D\u6709\u9009\u62E9\u7684\u673A\u4F1A\u3002\u8F6C\u7BA1\u7406\u662F\u6709\u95E8\u69DB\u7684\uFF0C\u53D1\u5C55\u8DEF\u5F84\u4F1A\u4E00\u822C\u662F\u6280\u672F\u4E13\u5BB6-\u6280\u672Fleader-\u7BA1\u7406\u3002\uFF1B</p><p>\u7B2C\u4E8C\u70B9\uFF0C\u9700\u8981\u5B66\u4E60\u76F8\u5E94\u7684\u8F6F\u6280\u80FD\u3002\u8FD9\u4E2A\u6DB5\u76D6\u7684\u8303\u56F4\u8FD8\u633A\u5927\uFF0C\u7B80\u5355\u5217\u4E3E\u4E9B\uFF1A\u5982\u4F55\u62DB\u4EBA\u3001\u5982\u4F55\u7528\u4EBA\u3001\u5982\u4F55\u4FDD\u4EBA\uFF0C\u9886\u5BFC\u529B\uFF0C\u6C9F\u901A\u80FD\u529B\uFF0C\u9879\u76EE\u7BA1\u7406\u80FD\u7B49\u7B49\u3002</p><p>\u5982\u679C\u4F60\u672A\u6765\u60F3\u8981\u8D70\u5411\u7BA1\u7406\u5C97\uFF0C\u5728\u6280\u672F\u4E0A\u4E00\u5B9A\u8981\u6709\u5EFA\u6811\uFF0C\u8FD8\u8981\u63D0\u524D\u5B66\u4E60\u548C\u50A8\u5907\u76F8\u5E94\u7684\u8F6F\u6280\u80FD\uFF0C\u800C\u4E0D\u662F\u8F6C\u8FC7\u4E4B\u540E\u5728\u8865\u5145\u3002</p><h2 id="_03-\u503C\u5F97\u601D\u8003\u7684\u95EE\u9898" tabindex="-1"><a class="header-anchor" href="#_03-\u503C\u5F97\u601D\u8003\u7684\u95EE\u9898" aria-hidden="true">#</a> 03.\u503C\u5F97\u601D\u8003\u7684\u95EE\u9898</h2><img src="https://www.hualigs.cn/image/60b88fbd52e75.jpg" style="zoom:60%;" align="center"><p>\u8FD9\u6B21\u5206\u4EAB\u4E0D\u662F\u4E3A\u4E86\u7ED9\u5927\u5BB6\u5177\u4F53\u7684\u7B54\u6848\uFF0C\u800C\u662F\u5E0C\u671B\u80FD\u591F\u5F15\u53D1\u5927\u5BB6\u5BF9\u4E8E\u7B97\u6CD5\u5DE5\u7A0B\u5E08/\u7814\u7A76\u5458\u804C\u4E1A\u53D1\u5C55\u95EE\u9898\u7684\u601D\u8003\u3002\u4EBA\u65E0\u8FDC\u8651\u5FC5\u6709\u8FD1\u5FE7\uFF0C\u60F3\u6E05\u695A\u540E\u65E9\u505A\u76F8\u5E94\u7684\u89C4\u5212\u548C\u51C6\u5907\u3002\u63A5\u4E0B\u6765\u4F1A\u7ED9\u5927\u5BB6\u63D0\u51FA\u51E0\u4E2A\u95EE\u9898\uFF1A</p><p><strong>1. Deep and wide, which domain?</strong></p><p>\u8FD9\u4E2A\u95EE\u9898\u5728\u4E4B\u524D\u8BA8\u8BBA\u8FC7\uFF0C\u5927\u5BB6\u53EF\u4EE5\u56DE\u987E\u4E0B\u3002\u5E0C\u671B\u4F60\u5728\u5E73\u65F6\u5BF9\u8FD9\u4E2A\u95EE\u9898\u5C31\u6709\u6DF1\u5165\u7684\u601D\u8003\uFF0C\u4E0D\u8981\u5230\u4E09\u4E94\u5E74\u4E4B\u540E\u88AB\u95EE\u5230\u7684\u65F6\u5019\u56DE\u7B54\u201C\u6211\u8FD8\u6CA1\u6709\u60F3\u597D\u201D\u3002\u627E\u4E0D\u5230\u65B9\u5411\u65F6\uFF0C\u5F80\u54EA\u4E2A\u65B9\u5411\u8D70\u90FD\u662F\u9519\u8BEF\u7684\u3002</p><p><strong>2. How much do i want to work with people? And how good am i?</strong></p><p>\u5927\u90E8\u5206\u4EBA\u90FD\u6709\u8F6C\u7BA1\u7406\u7684\u60F3\u6CD5\uFF0C\u4F46\u662F\u4F60\u4E00\u5B9A\u8981\u6E05\u695A\u505A\u7814\u53D1\u4E0E\u505A\u7BA1\u7406\u662F\u5F88\u4E0D\u4E00\u6837\u7684\u3002\u7B80\u5355\u63D0\u9192\u51E0\u70B9\uFF1A</p><p>\u7B2C\u4E00\uFF0C\u4F55\u65F6\u9009\u62E9\u8F6C\u7BA1\u7406\uFF1F\u5982\u4F55\u4ECE\u6280\u672F\u4E13\u5BB6\u8FC7\u6E21\u5230\u7BA1\u7406\u5C97\u4F4D\uFF1F\u8FD9\u662F\u9700\u8981\u9762\u5BF9\u7684\u7B2C\u4E00\u4E2A\u95EE\u9898\uFF1B</p><p>\u7B2C\u4E8C\uFF0C\u5982\u4F55\u83B7\u53D6\u56E2\u961F\u7684\u4FE1\u4EFB\uFF1F\u662F\u4E0D\u662F\u53EA\u8981\u4F60\u7684\u6280\u672F\u8DB3\u591F\u5F3A\uFF0C\u5C31\u53EF\u4EE5\u83B7\u53D6\u56E2\u961F\u6210\u5458\u7684\u8BA4\u53EF\u548C\u5C0A\u656C\uFF0C\u5C31\u53EF\u4EE5\u628A\u8001\u677F\u4EA4\u4EE3\u7684\u4EFB\u52A1\u90FD\u5E72\u597D\u3002\u662F\u4E0D\u662F\u771F\u7684\u662F\u8FD9\u6837\u5B50\u7684\uFF1F</p><p>\u7B2C\u4E09\uFF0C\u5982\u4F55\u5C3D\u5FEB\u8FDB\u5165\u7BA1\u7406\u89D2\u8272\uFF1F\u4F60\u8981\u5145\u5206\u610F\u8BC6\u5230\u7BA1\u7406\u5DE5\u4F5C\u66F4\u591A\u662F\u548C\u4EBA\u6253\u4EA4\u9053\uFF0C\u5305\u62EC\u4E0A\u7EA7\u8001\u677F\u3001\u4E00\u8D77\u914D\u5408\u7684\u5144\u5F1F\u56E2\u961F\u4EE5\u53CA\u4F60\u6240\u5E26\u9886\u7684\u56E2\u961F\u6210\u5458\uFF0C\u6D89\u53CA\u5230\u5F88\u591A\u4E0E\u4EBA\u6C9F\u901A\u7684\u7EC6\u8282\uFF0C\u4F60\u8BE5\u5982\u4F55\u5E94\u5BF9\u3002</p><p><strong>3. How to balance engineering skills vs research skills?</strong></p><p>\u8FD9\u4E2A\u95EE\u9898\u662F\u7B97\u6CD5\u5DE5\u7A0B\u5E08\u7FA4\u4F53\u4EEC\u666E\u904D\u90FD\u4F1A\u9047\u5230\u7684\uFF0C\u7B97\u6CD5\u7814\u7A76\u80FD\u529Bor\u5DE5\u7A0B\u80FD\u529B\u3002\u60A8\u5728\u5176\u4E2D\u4E00\u4E2A\u65B9\u5411\u4E0A\u6210\u4E3A\u4E13\u5BB6\u662F\u6CA1\u6709\u95EE\u9898\uFF0C\u4F01\u4E1A\u90FD\u4F1A\u6709\u5408\u9002\u7684\u4F4D\u7F6E\u7ED9\u5230\u4F60\u3002\u4F46\u5982\u679C\u4F60\u60F3\u8D70\u5230\u66F4\u9AD8\u7684\u804C\u7EA7\uFF0C\u5C31\u9700\u8981\u4E0D\u65AD\u62D3\u5BBD\u4E0E\u73B0\u6709\u5DE5\u4F5C\u76F8\u5173\u7684\u6280\u672F\u6808\u3002\u5927\u5BB6\u53EF\u4EE5\u56DE\u5230\u5F00\u5934\u8BB2\u7684\u56E2\u961F\u67B6\u6784\u56FE\uFF0C\u4F60\u4F1A\u53D1\u73B0\u5728\u7EC4\u7EC7\u7684\u7279\u5B9A\u5C42\u7EA7\u7684\u7279\u5B9A\u4F4D\u7F6E\uFF0C\u7B97\u6CD5\u7814\u7A76\u548C\u5DE5\u7A0B\u80FD\u529B\u4F1A\u7ED3\u5408\u8D77\u6765\uFF0C\u8FD9\u5C31\u8981\u6C42\u4F4D\u4E8E\u8FD9\u4E2A\u4F4D\u7F6E\u7684\u4EBA\u5FC5\u987B\u517C\u987E\u4E24\u4E2A\u65B9\u5411\uFF0C\u4E24\u4E2A\u65B9\u5411\u90FD\u8981\u901A\uFF0C\u5426\u5219\u5F88\u96BE\u505A\u5230\u628A\u4E24\u4E2A\u65B9\u5411\u7684\u56E2\u961F\u5E26\u597D\u3002</p><p>\u968F\u7740\u8FD1\u4E24\u5E74\u7B97\u6CD5\u5C97\u4F4D\u7684\u6210\u719F\uFF0C\u6709\u4E00\u79CD\u8BF4\u6CD5\u91CD\u65B0\u88AB\u5927\u5BB6\u8BA4\u540C\uFF0C\u90A3\u5C31\u662F\u201C\u7B97\u6CD5\u5DE5\u7A0B\u5E08\u9996\u5148\u662F\u4E00\u540D\u5DE5\u7A0B\u5E08\u201D\uFF0C\u53EF\u89C1\u5BF9\u7B97\u6CD5\u5DE5\u7A0B\u5E08\u5DE5\u7A0B\u80FD\u529B\u7684\u91CD\u89C6\u5DF2\u7ECF\u662F\u666E\u904D\u7684\u89C2\u70B9\u4E86\u3002</p><p><strong>4. How to balance working on the real problems vs exploring advanced tech?</strong></p><p>\u5BF9\u4E8E\u8FD9\u70B9\uFF0C\u5B83\u4E0D\u662F\u4E00\u8FB9\u597D\u4E8E\u53E6\u5916\u4E00\u8FB9\u7684\u95EE\u9898\uFF0C\u66F4\u591A\u7684\u662F\u73AF\u5883\u5851\u9020\u6216\u8005\u4E2A\u4EBA\u504F\u597D\u3002\u5C24\u5176\u8FD1\u51E0\u5E74\uFF0C\u4F01\u4E1A\u5BF9\u7B97\u6CD5\u5E94\u7528\u7684\u9700\u6C42\u6FC0\u589E\uFF0C\u4F46\u504F\u504F\u4EBA\u624D\u4F9B\u7ED9\u77ED\u671F\u5185\u53C8\u5F88\u96BE\u88AB\u6EE1\u8DB3\uFF0C\u5BFC\u81F4\u5F88\u591A\u7B97\u6CD5\u540C\u5B66\u4E00\u4E2A\u9879\u76EE\u63A5\u4E00\u4E2A\u9879\u76EE\u7684\u505A\u4E0B\u53BB\uFF0C\u89E3\u51B3\u4E86\u4E00\u4E2A\u53C8\u4E00\u4E2A\u7684\u5B9E\u9645\u95EE\u9898\uFF0C\u5728\u8FD9\u4E2A\u8FC7\u7A0B\u4E2D\u81EA\u7136\u79EF\u6512\u4E86\u5927\u91CF\u7684\u5B9E\u9645\u7ECF\u9A8C\uFF1B\u4F46\u662F\u53E6\u5916\u4E00\u4E9B\u8EAB\u5728Ai Lab\u7684\u7B97\u6CD5\u540C\u5B66\u5C31\u4E0D\u4E00\u6837\u4E86\uFF0C\u9876\u4F1Apaper\u4E00\u7BC7\u63A5\u4E00\u7BC7\u7684\u53D1\uFF0C\u4FDD\u6301\u7740\u5BF9\u65B0\u6280\u672F\u65B0\u53D1\u73B0\u7684\u6301\u7EED\u8DDF\u8FDB\u3002\u4F60\u5F88\u96BE\u8BF4\u4E00\u4E2A\u597D\u8FC7\u53E6\u5916\u4E00\u4E2A\uFF0C\u9700\u8981\u8BA4\u6E05\u4E2A\u4EBA\u66F4\u559C\u6B22\u54EA\u7C7B\u5DE5\u4F5C\uFF0C\u4EE5\u53CA\u5728\u7279\u5B9A\u7684\u73AF\u5883\u4E0B\u505A\u7B26\u5408\u9884\u671F\u7684\u4E8B\u60C5\u3002</p><p><strong>5. How to develop methodology over time so you can solve larger and more complex problems?</strong></p><p>\u6700\u540E\u662F\u5173\u4E8E\u53D1\u5C55\u4E2A\u4EBA\u65B9\u6CD5\u8BBA\u7684\u95EE\u9898\uFF0C\u8FD9\u4E2A\u95EE\u9898\u6211\u5728\u5185\u90E8\u4E5F\u7ECF\u5E38\u63D0\u8D77\u3002\u62FF\u6211\u4EEC\u56E2\u961F\u4E3E\u4F8B\u5B50\uFF0C\u4F60\u662F\u56E2\u961F\u7684\u4E00\u4E2Areasearcher\uFF0C\u4E1A\u754C\u4E00\u4E9B\u524D\u6CBF\u7684\u6280\u672F\u6210\u679C\u90FD\u8DDF\u4E0A\u4E86\uFF0C\u90A3\u4F60\u5982\u4F55\u624D\u80FD\u5728\u6B64\u57FA\u7840\u4E0A\u505A\u7684\u66F4\u597D\u5462\uFF1F</p><p>\u5927\u5BB6\u53EF\u4EE5\u601D\u8003\u8FD9\u6837\u4E00\u4E2A\u95EE\u9898\uFF0C\u5DE5\u4F5C\u7ECF\u9A8C\u5206\u522B\u4E3A1\u5E74\u30015\u5E74\u300110\u5E74\u7684\u4E09\u4E2A\u5DE5\u7A0B\u5E08\u5728\u89E3\u51B3\u540C\u4E00\u4E2A\u95EE\u9898\u65F6\u7684\u5DEE\u522B\u662F\u4EC0\u4E48\u5462\uFF1F\u7ED3\u679C\u53EF\u80FD\u90FD\u4E00\u6837\uFF0C\u90A3\u5C31\u662F\u95EE\u9898\u90FD\u88AB\u89E3\u51B3\u4E86\uFF0C\u4F46\u771F\u6B63\u7684\u5DEE\u522B\u5728\u4E8E\u5927\u5BB6\u770B\u95EE\u9898\u7684\u89D2\u5EA6\u548C\u7EF4\u5EA6\u4E0D\u4E00\u6837\u3002\u4F5C\u4E3A\u5DE5\u4F5C\u7ECF\u9A8C\u4E45\u7684\u5DE5\u7A0B\u5E08\uFF0C\u4F60\u770B\u5230\u7684\u4E0D\u5E94\u8BE5\u4EC5\u4EC5\u662F\u95EE\u9898\u7684\u8868\u9762\uFF0C\u66F4\u8981\u770B\u5230\u95EE\u9898\u7684\u524D\u56E0\u540E\u679C\uFF0C\u4EE5\u53CA\u5176\u4ED6\u4E0E\u6B64\u76F8\u5173\u7684\u4E8B\u60C5\u3002\u4E3A\u4EC0\u4E48\u4F1A\u8FD9\u6837\u5462\uFF1F\u56E0\u4E3A\u901A\u8FC7\u591A\u5E74\u7684\u5DE5\u4F5C\u79EF\u7D2F\uFF0C\u8D44\u6DF1\u7684\u5DE5\u7A0B\u5E08\u5DF2\u7ECF\u6709\u4E86\u89E3\u51B3\u95EE\u9898\u7684\u6846\u67B6\uFF0C\u53EF\u4EE5\u628A\u95EE\u9898\u653E\u5728\u6846\u67B6\u4E0B\u62C6\u89E3\u5206\u6790\uFF0C\u6700\u540E\u627E\u5230\u89E3\u51B3\u95EE\u9898\u7684\u65B9\u6CD5\u3002</p><p>\u968F\u7740\u5DE5\u4F5C\u5E74\u9650\u7684\u589E\u52A0\uFF0C\u5927\u5BB6\u4E00\u5B9A\u8981\u603B\u7ED3\u5E76\u63D0\u70BC\u51FA\u4E0E\u5DE5\u9F84\u76F8\u5339\u914D\u7684\u65B9\u6CD5\u8BBA\uFF0C\u5426\u5219\u4F60\u662F\u65E0\u6CD5\u5BF9\u201C\u540E\u6D6A\u201D\u5F62\u6210\u7ADE\u4E89\u4F18\u52BF\u3002\u5F62\u6210\u4E86\u81EA\u5DF1\u7684\u65B9\u6CD5\u8BBA\u540E\uFF0C\u4E0D\u4EC5\u53EF\u4EE5\u66F4\u9AD8\u6548\u7684\u5E94\u5BF9\u8001\u95EE\u9898\uFF0C\u8FD8\u80FD\u4ECE\u5BB9\u7684\u9762\u5BF9\u65B0\u95EE\u9898\u3002\u4E3A\u4EC0\u4E48\u4F1A\u5462\uFF1F\u56E0\u4E3A\u8001\u95EE\u9898\u53EF\u4EE5\u76F4\u63A5\u5957\u7528\u73B0\u6709\u89E3\u51B3\u65B9\u6848\uFF0C\u6548\u7387\u80AF\u5B9A\u63D0\u9AD8\uFF1B\u9762\u5BF9\u65B0\u95EE\u9898\u65F6\uFF0C\u5C06\u95EE\u9898\u653E\u5728\u73B0\u6709\u7684\u65B9\u6CD5\u8BBA\u4E0B\u8FDB\u884C\u62C6\u89E3\uFF0C\u5F88\u5FEB\u4E5F\u4F1A\u627E\u5230\u65B9\u6848\u3002\u4F60\u4E00\u5B9A\u8981\u77E5\u9053\uFF0C\u968F\u7740\u804C\u7EA7\u589E\u52A0\u7684\u4E0D\u4EC5\u662F\u6536\u5165\uFF0C\u8FD8\u6709\u9700\u8981\u5904\u7406\u95EE\u9898\u7684\u6570\u91CF\u3002\u5982\u679C\u6CA1\u6709\u79EF\u7D2F\uFF0C\u4F60\u5728\u5E94\u4ED8\u6FC0\u589E\u7684\u95EE\u9898\u65F6\u4F1A\u663E\u5F97\u5F88\u5403\u529B\u3002</p><p><strong>\u4ECA\u5929\u7684\u5206\u4EAB\u5C31\u5230\u8FD9\u91CC\uFF0C\u5E0C\u671B\u901A\u8FC7\u8FD9\u4E9B\u95EE\u9898\u5F15\u53D1\u4F60\u5BF9\u804C\u4E1A\u53D1\u5C55\u7684\u601D\u8003\uFF0C\u65E9\u65E5\u627E\u5230\u9002\u5408\u81EA\u5DF1\u7684\u53D1\u5C55\u8DEF\u5F84\uFF0C\u8C22\u8C22\u5927\u5BB6~</strong></p><p>[TOC]</p>',59),n=[s];function a(g,i){return p(),t("div",null,n)}const h=e(r,[["render",a],["__file","article-4.html.vue"]]);export{h as default};
